INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14016, 5888, 'Oxaliplatin', 'Lung Diseases', 'Oxaliplatin has been associated with pulmonary fibrosis (<1% of study patients), which may be fatal.  Caution should be exercised when administering oxaliplatin in patients with compromised pulmonary function.  Patients should be instructed to immediately report symptoms of unexplained respiratory symptoms such as non-productive cough, dyspnea, crackles, or radiological pulmonary infiltrates.  Close clinical monitoring of pulmonary function is recommended and oxaliplatin should be discontinued until further pulmonary investigation excludes interstitial lung disease or pulmonary fibrosis.', '3', '"Product Information. Eloxatin (oxaliplatin)." Sanofi Winthrop Pharmaceuticals  (2002):', 'DDInter', 0);
