INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14126, 9483, 'Oxazepam', 'Obesity', 'The plasma half-lives of benzodiazepines may be prolonged in obese patients, presumably due to increased distribution into fat.  Marked increases in distribution (> 100%) have been reported for diazepam and midazolam, and moderate increases (25% to 100%) for alprazolam, lorazepam, and oxazepam.  Therapy with benzodiazepines should be administered cautiously in obese patients, with careful monitoring of CNS status.  Longer dosing intervals may be appropriate.  When dosing by weight, loading doses should be based on actual body weight, while maintenance dose should be based on ideal body weight to avoid toxicity.', '2', '"Product Information. Xanax (alprazolam)." Pharmacia and Upjohn  (2002):|"Product Information. Valium (diazepam)." Roche Laboratories  (2002):|"Product Information. Ativan (lorazepam)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Serax (oxazepam)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Restoril (temazepam)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Halcion (triazolam)." Pharmacia and Upjohn  (2001):|"Product Information. Dalmane (flurazepam)." Roche Laboratories  (2001):|"Product Information. Tranxene (clorazepate)." Abbott Pharmaceutical  (2001):|"Product Information. Klonopin (clonazepam)." Roche Laboratories  (2001):|"Product Information. Prosom (estazolam)." Abbott Pharmaceutical  (2022):|"Product Information. Librium (chlordiazepoxide)." Roche Laboratories  (2001):|"Product Information. Doral (quazepam)." Wallace Laboratories  (2001):|"Product Information. Versed (midazolam)." Roche Laboratories  (2001):|American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association;  (1994):', 'DDInter', 0);
