INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (14944, 0, 'Auranofin', 'Kidney Diseases', 'The use of gold compounds is contraindicated in patients with chronic renal failure.  These patients may be at increased risk for renal toxicity.  Prior to gold compounds therapy, a complete blood count with a differential, platelet count, urinalysis, and renal and liver function tests should be performed.  A complete blood count with a differential, platelet count, and urinalysis should be done at least monthly during therapy.', '3', '"Product Information. Ridaura (auranofin)." SmithKline Beecham  (2001):|"Product Information. Solganal (aurothioglucose)." Schering Corporation  (2001):|"Product Information. Myochrysine (gold sodium thiomalate)." Merck & Co., Inc  (2022):', 'DDInter', 0);
