INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7052, 14411, 'Armodafinil', 'Alcoholism', 'Patients with excessive sleepiness, including those taking armodafinil, should be frequently reassessed for their degree of sleepiness and, if appropriate, advised to avoid driving or any other potentially dangerous activity.  Patients should refrain from drinking alcohol while taking armodafinil as any drug affecting the central nervous system (CNS) may alter judgment, thinking or motor skills.', '2', '"Product Information. Nuvigil (armodafinil)." Cephalon Inc  (2007):', 'DDInter', 0);
