INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7175, 2971, 'Methylphenidate', 'Intestinal Obstruction', 'The extended-release formulation of methylphenidate (Concerta) contains a non-deformable material.  There have been rare reports of obstructive symptoms in patients with known strictures following the ingestion of similar sustained-release products.  The extended-release formulation of methylphenidate should ordinarily not be administered in patients with preexisting severe gastrointestinal narrowing or obstruction, whether pathologic or iatrogenic (e.g., small bowel inflammatory disease, "short gut" syndrome due to adhesions or decreased transit time, past history of peritonitis, cystic fibrosis, chronic intestinal pseudo-obstruction, or Meckel''s diverticulum).  The medication should only be used in patients who are able to swallow the tablet whole.', '2', '"Product Information. Concerta (methylphenidate)." Alza  (2002):', 'DDInter', 0);
