INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7454, 7196, 'Methyltestosterone', 'Polycythemia', 'Androgenic anabolic steroids may cause polycythemia when given in high dosages or for prolonged periods.  Patients with preexisting polycythemia may experience worsening of their condition.  Frequent monitoring of clinical status and hemoglobin and hematocrit levels is recommended if androgen therapy is administered to these patients.', '3', '"Product Information. Halotestin (fluoxymesterone)." Pharmacia and Upjohn  (2001):|"Product Information. Androderm (testosterone topical)." SmithKline Beecham  (2001):', 'DDInter', 0);
