INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15293, 26948, 'Pasireotide', 'Long QT Syndrome', 'The use of pasireotide is associated with QT prolongation.  Pasireotide should be used with caution in patients who are at significant risk of developing QT prolongation.  It is recommended to correct levels of hypokalemia and hypomagnesemia prior to starting therapy with this agent.  Assess ECG at the beginning of therapy and monitor during therapy.  Care should be taken in predisposed patients.', '2', '"Product Information. Signifor (pasireotide)." Novartis Pharmaceuticals  (2013):', 'DDInter', 0);
