INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15308, 20291, 'Pegfilgrastim', 'Glomerulonephritis', 'Although it has been reported that renal dysfunction has no effect on the pharmacokinetics of colony stimulating factors (CSF), filgrastim and pegfilgrastim may cause glomerulonephritis.  Symptoms include swelling of the face or ankles, dark colored urine, or blood in the urine, or a decrease in urine production.  Advise patients to report signs or symptoms of glomerulonephritis immediately.  Monitoring of patients with renal dysfunction is recommended.', '3', '"Product Information. Neupogen (filgrastim)." Amgen  (2002):|"Product Information. Neulasta (pegfilgrastim)." Amgen  (2002):|"Product Information. Granix (tbo-filgrastim)." Teva Pharmaceuticals USA  (2013):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15309, 23444, 'Pegfilgrastim', 'Glomerulonephritis', 'Although it has been reported that renal dysfunction has no effect on the pharmacokinetics of colony stimulating factors (CSF), filgrastim and pegfilgrastim may cause glomerulonephritis.  Symptoms include swelling of the face or ankles, dark colored urine, or blood in the urine, or a decrease in urine production.  Advise patients to report signs or symptoms of glomerulonephritis immediately.  Monitoring of patients with renal dysfunction is recommended.', '3', '"Product Information. Neupogen (filgrastim)." Amgen  (2002):|"Product Information. Neulasta (pegfilgrastim)." Amgen  (2002):|"Product Information. Granix (tbo-filgrastim)." Teva Pharmaceuticals USA  (2013):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15310, 20291, 'Pegfilgrastim', 'Leukemia, Myeloid', 'Colony stimulating factors primarily stimulates the proliferation of neutrophils but may also, theoretically, enhance tumor growth, particularly in myeloid malignancies.  Therapy with these drugs should be administered cautiously in patients with myeloid tumors.  Additionally, if used for PBPC (peripheral blood progenitor cell) collection in such patients, tumor cells may be released from the marrow and collected in the leukapheresis product.  The effect of reinfusion of tumor cells is uncertain at this time.', '3', '"Product Information. Neupogen (filgrastim)." Amgen  (2002):|"Product Information. Neulasta (pegfilgrastim)." Amgen  (2002):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15311, 23444, 'Pegfilgrastim', 'Leukemia, Myeloid', 'Colony stimulating factors primarily stimulates the proliferation of neutrophils but may also, theoretically, enhance tumor growth, particularly in myeloid malignancies.  Therapy with these drugs should be administered cautiously in patients with myeloid tumors.  Additionally, if used for PBPC (peripheral blood progenitor cell) collection in such patients, tumor cells may be released from the marrow and collected in the leukapheresis product.  The effect of reinfusion of tumor cells is uncertain at this time.', '3', '"Product Information. Neupogen (filgrastim)." Amgen  (2002):|"Product Information. Neulasta (pegfilgrastim)." Amgen  (2002):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15312, 20291, 'Pegfilgrastim', 'Leukocytosis', 'Filgrastim and pegfilgrastim are growth factors for neutrophil progenitor cells.  Therefore, these should not be used in patients with leukocytosis.  If excessive white blood cell counts occur during CSF therapy, treatment should be interrupted or the dosage reduced.', '3', 'Hollingshead LM, Goa KL "Recombinant granulocyte colony-stimulating factor (rG-CSF). A review of its pharmacological properties and prospective role in neutropenic conditions." Drugs 42 (1991):  300-30|"Product Information. Neupogen (filgrastim)." Amgen  (2002):|"Product Information. Neulasta (pegfilgrastim)." Amgen  (2002):', 'DDInter', 0);
