INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15689, 16931, 'Penicillamine', 'Kidney Diseases', 'Penicillamine is considerably excreted by the kidney, and the risk of toxic reactions to this drug may be greater in patients with impaired renal function.  The use of penicillamine is contraindicated in patients with renal dysfunction.  Proteinuria and/or hematuria may develop during therapy and may lead to nephrotic syndrome.  Monitoring for proteinuria and hematuria is recommended.  Caution should be taken and routine monitoring of renal function is recommended.', '3', '"Product Information. Cuprimine (penicillamine)." Merck & Co., Inc  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15690, 25939, 'Penicillamine', 'Kidney Diseases', 'Penicillamine is considerably excreted by the kidney, and the risk of toxic reactions to this drug may be greater in patients with impaired renal function.  The use of penicillamine is contraindicated in patients with renal dysfunction.  Proteinuria and/or hematuria may develop during therapy and may lead to nephrotic syndrome.  Monitoring for proteinuria and hematuria is recommended.  Caution should be taken and routine monitoring of renal function is recommended.', '3', '"Product Information. Cuprimine (penicillamine)." Merck & Co., Inc  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15691, 16931, 'Penicillamine', 'Liver Diseases', 'Cases of intrahepatic cholestasis and toxic hepatitis have been reported with the use of penicillamine.  Care should be taken and liver function tests are recommended every six months for the duration of therapy.', '2', '"Product Information. Cuprimine (penicillamine)." Merck & Co., Inc  (2001):', 'DDInter', 0);
