INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15754, 0, 'Avatrombopag', 'Kidney Failure, Chronic', 'The effect of severe renal impairment (CrCl < 30 mL/min) including patients requiring hemodialysis on avatrombopag pharmacokinetics is unknown.  Caution and monitoring is advised.', '2', '"Product Information. Doptelet (avatrombopag)." Dova Pharmaceuticals  (2018):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15755, 0, 'Avatrombopag', 'Thromboembolism', 'Thromboembolic complications may result from increases in platelet counts with TPO receptor agonists such as avatrombopag, lusutrombopag, and eltrombopag.  Consider the potential for increased risk of thromboembolism when administering these medications to patients with known risk factors for thromboembolism, including genetic prothrombotic conditions (Factor V Leiden, Prothrombin 20210 A, Antithrombin deficiency or Protein C or S deficiency).', '2', '"Product Information. Promacta (eltrombopag)." GlaxoSmithKline  (2008):|"Product Information. Doptelet (avatrombopag)." Dova Pharmaceuticals  (2018):|"Product Information. Mulpleta (lusutrombopag)." Shionogi USA Inc  (2018):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15756, 0, 'Perflutren', 'Cardiovascular Diseases', 'Serious cardiopulmonary reactions including fatalities have uncommonly occurred during or shortly following perflutren- containing microsphere administration, typically within 30 minutes of administration.  The risk may be increased among patients with unstable cardiopulmonary conditions such as acute myocardial infarction, acute coronary artery syndromes, worsening or unstable congestive heart failure, or serious ventricular arrhythmias.  Always have cardiopulmonary resuscitation personnel and equipment readily available prior to perflutren administration and monitor all patients for acute reactions.', '3', '"Product Information. Definity (perflutren)." Lantheus Medical Imaging Inc  (2015):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15757, 0, 'Perflutren', 'Hepatic Insufficiency', 'The pharmacokinetics of activated perflutren has not been studied in subjects with hepatic disease.  Caution is advised if used in these patients.', '2', '"Product Information. Definity (perflutren)." Lantheus Medical Imaging Inc  (2015):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15758, 11667, 'Perphenazine', 'Dementia', 'Older patients with dementia-related psychosis treated with antipsychotic drugs are at an increased risk of death; although the causes were varied, most of the deaths appeared to be either cardiovascular (e.g., heart failure, sudden death) or infectious (e.g., pneumonia) in nature.  A causal relationship with antipsychotic use has not been established.  In controlled trials in older patients with dementia-related psychosis, patients randomized to risperidone, aripiprazole, and olanzapine had higher incidence of cerebrovascular adverse events (e.g., stroke, transient ischemic attack), including fatalities, compared to patients treated with placebo.  These agents are not approved for the treatment of patients with dementia-related psychosis.', '3', '"Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals  (2001):|"Product Information. Risperdal (risperidone)." Janssen Pharmaceuticals  (2001):|"Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company  (2001):|"Product Information. Seroquel (quetiapine)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Geodon (ziprasidone)." Pfizer U.S. Pharmaceuticals  (2001):|"Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb  (2002):|"Product Information. Invega (paliperidone)." Janssen Pharmaceuticals  (2007):|"Product Information. Fanapt (iloperidone)." Vanda Pharmaceuticals Inc  (2009):|"Product Information. Saphris (asenapine)." Schering-Plough Corporation  (2009):|"Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc  (2010):|"Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc  (2015):|"Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc.  (2015):|"Product Information. Nuplazid (pimavanserin)." Accelis Pharma  (2016):|"Product Information. Caplyta (lumateperone)." Intra-Cellular Therapies, Inc.  (2022):', 'DDInter', 0);
