INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15808, 29604, 'Pazopanib', 'Liver Diseases', 'Severe and fatal hepatotoxicity has been observed with the use of pazopanib.  No dose adjustment is required in patients with mild hepatic impairment.  Pazopanib dose should be reduced to 200 mg per day in patients with moderate hepatic impairment.  Pazopanib is not recommended in patients with severe hepatic impairment.  It is recommended to monitor serum liver tests before starting treatment with pazopanib and at weeks 3, 5, 7, and 9.  Thereafter, monitor at month 3 and at month 4, and as clinically indicated; periodic monitoring should then continue monthly.  The use of this agent should be interrupted in patients presenting significant hepatic dysfunction and restart therapy only if clinically necessary and according to clinical recommendations.', '3', '"Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):', 'DDInter', 0);
