INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15814, 29604, 'Pazopanib', 'Hypothyroidism', 'Hypothyroidism has been reported in patients taking pazopanib.  Therapy with pazopanib should be administered cautiously to patients with thyroid dysfunction.  Baseline laboratory measurement of thyroid function is recommended and if appropriate, treatment per standard medical practice should be given prior to initiation of therapy.  Clinical monitoring of thyroid function is recommended during therapy.', '2', '"Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):', 'DDInter', 0);
