INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15816, 29604, 'Pazopanib', 'Long QT Syndrome', 'Pazopanib can cause QT prolongation which may lead to an increased risk for ventricular arrhythmias, including Torsade de Pointes.  This agent should be administered with caution in patients with a history of QT interval prolongation, in patients taking antiarrhythmics or other medications that may prolong QT interval.  Serum electrolytes should be corrected before initiating therapy with this agent.  Periodic monitoring of ECGs and electrolytes is recommended in these patients.', '2', '"Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):', 'DDInter', 0);
