INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15820, 29604, 'Pazopanib', 'Posterior Leukoencephalopathy Syndrome', 'Posterior Reversible Encephalopathy Syndrome (PRES) has been reported with the use of inhibitors of vascular endothelial growth factor receptors (VEGFR) in clinical studies.  PRES is a neurological disorder which can present with headache, seizure, lethargy, confusion, blindness and other visual and neurologic disturbances.  It is recommended to discontinue these agents in patients developing PRES.  The safety of restarting therapy is not known.  Cation is recommended if therapy is reinitiating in patients previously experiencing PRES.', '2', '"Product Information. Avastin (bevacizumab)." Genentech  (2004):|"Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):|"Product Information. Cyramza (ramucirumab)." Eli Lilly and Company  (2014):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15821, 29605, 'Pazopanib', 'Posterior Leukoencephalopathy Syndrome', 'Posterior Reversible Encephalopathy Syndrome (PRES) has been reported with the use of inhibitors of vascular endothelial growth factor receptors (VEGFR) in clinical studies.  PRES is a neurological disorder which can present with headache, seizure, lethargy, confusion, blindness and other visual and neurologic disturbances.  It is recommended to discontinue these agents in patients developing PRES.  The safety of restarting therapy is not known.  Cation is recommended if therapy is reinitiating in patients previously experiencing PRES.', '2', '"Product Information. Avastin (bevacizumab)." Genentech  (2004):|"Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):|"Product Information. Cyramza (ramucirumab)." Eli Lilly and Company  (2014):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15822, 29604, 'Pazopanib', 'Proteinuria', 'The incidence and severity of proteinuria is increased in patients taking inhibitors of vascular endothelial growth factor receptors (VEGFR).  Therapy with these agents should be administered cautiously in patients with renal dysfunction.  Monitoring for proteinuria and hematuria is recommended and perform baseline and periodic urinalyses during treatment, with follow up measurement as clinically indicated.  It is recommended to temporarily suspend these agents in patients with moderate to severe proteinuria and to discontinue therapy in patients with nephrotic syndrome.', '2', '"Product Information. Avastin (bevacizumab)." Genentech  (2004):|"Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):|"Product Information. Cyramza (ramucirumab)." Eli Lilly and Company  (2014):', 'DDInter', 0);
