INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15824, 29604, 'Pazopanib', 'Thromboembolism', 'Venous and arterial thromboembolic events (VTE and ATE), some fatal, have been reported with the use of inhibitors of vascular endothelial growth factor receptors (VEGFR).  Caution is recommended when using these agents in patients with a reported history of VTE or ATE.  These agents should be discontinued in patients with serious or life-threatening VTE or ATE, including pulmonary embolism.', '2', '"Product Information. Avastin (bevacizumab)." Genentech  (2004):|"Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):|"Product Information. Cyramza (ramucirumab)." Eli Lilly and Company  (2014):', 'DDInter', 0);
