INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, source) VALUES (7601, 0, 'Interferon alfa-2b', 'Heart Diseases', 'Hypertension, arrhythmias, angina, myocardial infarction, cardiomyopathy, and congestive heart failure have been reported during interferon therapy.  Therapy with interferons should be administered with caution in patients with compromised cardiac function.', '3', 'DDInter');
