INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7660, 8083, 'Methylergometrine', 'Liver Diseases', 'The amine ergot alkaloids, ergonovine and methylergonovine, appear to be eliminated by both renal and hepatic routes.  Patients with renal and/or liver disease may be at greater risk for adverse effects from these agents due to decreased drug clearance.  Caution is advised if these agents are used.  Dosage adjustments may be necessary.', '2', '"Product Information. Ergotrate Maleate (ergonovine)." Bedford Laboratories  (2001):|"Product Information. Methergine (methylergonovine)." Novartis Pharmaceuticals  (2010):', 'DDInter', 0);
