INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7735, 0, 'Acebutolol', 'Cerebrovascular Disorders', 'Beta-adrenergic blocking agents (beta-blockers), should be used with caution in patients with cerebrovascular insufficiency because of their potential effects relative to blood pressure and pulse.  If signs or symptoms suggesting reduced cerebral blood flow are observed, consideration should be given to discontinuing these agents.', '2', '"Product Information. Sectral (acebutolol)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Tenormin (atenolol)." ICN Pharmaceuticals Inc  (2002):|"Product Information. Trandate (labetalol)." Glaxo Wellcome  (2002):|"Product Information. Corgard (nadolol)." Bristol-Myers Squibb  (2002):|"Product Information. Inderal (propranolol)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Blocadren (timolol)." Merck & Co., Inc  (2001):|"Product Information. Brevibloc (esmolol)." DuPont Pharmaceuticals  (2001):|"Product Information. Cartrol (carteolol)." Abbott Pharmaceutical  (2001):|"Product Information. Kerlone (betaxolol)." Searle  (2001):|"Product Information. Lopressor (metoprolol)." Novartis Pharmaceuticals  (2001):|"Product Information. Betapace (sotalol)." Berlex Laboratories  (2001):|"Product Information. Zebeta (bisoprolol)." Lederle Laboratories  (2001):|"Product Information. Visken (pindolol)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Levatol (penbutolol)." Reed and Carnrick  (2001):|"Product Information. Coreg (carvedilol)." SmithKline Beecham  (2001):|"Product Information. Bystolic (nebivolol)." Forest Pharmaceuticals  (2007):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7736, 0, 'Acebutolol', 'Glaucoma', 'Systemic beta-adrenergic receptor blocking agents (aka beta-blockers) may lower intraocular pressure.  Therefore, patients with glaucoma or intraocular hypertension may require adjustments in their ophthalmic regimen following a dosing change or discontinuation of beta-blocker therapy.', '2', '"Product Information. Sectral (acebutolol)." Wyeth-Ayerst Laboratories  (2002):|"Product Information. Tenormin (atenolol)." ICN Pharmaceuticals Inc  (2002):|"Product Information. Normodyne (labetalol)." Schering Corporation  (2002):|"Product Information. Trandate (labetalol)." Glaxo Wellcome  (2002):|"Product Information. Corgard (nadolol)." Bristol-Myers Squibb  (2002):|"Product Information. Inderal (propranolol)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Blocadren (timolol)." Merck & Co., Inc  (2001):|"Product Information. Cartrol (carteolol)." Abbott Pharmaceutical  (2001):|"Product Information. Kerlone (betaxolol)." Searle  (2001):|"Product Information. Lopressor (metoprolol)." Novartis Pharmaceuticals  (2001):|"Product Information. Betapace (sotalol)." Berlex Laboratories  (2001):|"Product Information. Zebeta (bisoprolol)." Lederle Laboratories  (2001):|"Product Information. Visken (pindolol)." Sandoz Pharmaceuticals Corporation  (2001):|"Product Information. Levatol (penbutolol)." Reed and Carnrick  (2001):|"Product Information. Coreg (carvedilol)." SmithKline Beecham  (2001):|"Product Information. Bystolic (nebivolol)." Forest Pharmaceuticals  (2007):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7737, 0, 'Acebutolol', 'Hyperlipidemias', 'Beta-adrenergic receptor blocking agents (aka beta-blockers) may alter serum lipid profiles.  Increases in serum VLDL and LDL cholesterol and triglycerides, as well as decreases in HDL cholesterol, have been reported with some beta-blockers.  Patients with preexisting hyperlipidemia may require closer monitoring during beta-blocker therapy, and adjustments made accordingly in their lipid-lowering regimen.', '2', 'Rossner S, Weiner L "Atenolol and metoprolol: comparison of effects on blood pressure and serum lipoproteins, and side effects." Eur J Clin Pharmacol 24 (1983):  573-7|Valimaki M, Maass L, Harno K, Nikkila EA "Lipoprotein lipids and apoproteins during beta-blocker administration: comparison of penbutolol and atenolol." Eur J Clin Pharmacol 30 (1986):  17-20|Disler LJ, Joffe BI, Seftel HC "Massive hypertriglyceridemia associated with atenolol." Am J Med 85 (1988):  586-7|Harvengt C, Heller FR, Martiat P, Nieuwenhuyze YV "Short-term effects of beta blockers atenolol, nadolol, pindolol, and propranolol on lipoprotein metabolism in normolipemic subjects." J Clin Pharmacol 27 (1987):  475-80|Darga LL, Hakim MJ, Lucas CP, Franklin BA "Comparison of the effects of guanadrel sulfate and propranolol on blood pressure, functional capacity, serum lipoproteins and glucose in systemic hypertension." Am J Cardiol 67 (1991):  590-6|Rossner S, Weiner L "Atenolol and metoprolol: comparison of effects on blood pressure and serum lipoproteins, and side effects." Eur J Clin Pharmacol 24 (1983):  573-7|Weiner L, Rossner S "Atenolol 50 mg or metoprolol 200 mg: a comparison of antihypertensive efficacy, side effects and lipoprotein changes." Acta Med Scand 677 (1983):  153-7|Ferrara LA, Marotta T, Scilla A, et al. "Effect of oxprenolol and metoprolol on serum lipid concentration." Eur J