INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7752, 6075, 'Mexiletine', 'Kidney Diseases', 'Mexiletine is primarily eliminated by the kidney.  Approximately 10% of mexiletine is excreted in the urine unchanged.  Marked changes in urinary pH alter the rate of mexiletine excretion.  Elimination is accelerated with an acidic urinary pH and slowed with an alkaline urinary pH.  Therapy with mexiletine should be administered cautiously in patients with significant renal impairment.  Clinical monitoring of cardiac function (ECG) and renal function is recommended.', '2', 'Wang T, Wuellner D, Woosley RL, Stone WJ "Pharmacokinetics and nondialyzability of mexiletine in renal failure." Clin Pharmacol Ther 37 (1985):  649-53|El Allaf D, Henrard L, Crochelet L, Delapierre D, Carlier J, Dresse A "Pharmacokinetics of mexiletine in renal insufficiency." Br J Clin Pharmacol 14 (1982):  431-5|Guay DR, Meatherall RC, Ferguson I, Smith H, Light B "Mexiletine clearance during peritoneal dialysis ." Br J Clin Pharmacol 19 (1985):  857-8|Jones TE, Reece PA, Fisher GC "Mexiletine removal by peritoneal dialysis." Eur J Clin Pharmacol 25 (1983):  839-40|"Product Information. Mexitil (mexiletine)." Boehringer-Ingelheim  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7753, 8112, 'Mexiletine', 'Kidney Diseases', 'Mexiletine is primarily eliminated by the kidney.  Approximately 10% of mexiletine is excreted in the urine unchanged.  Marked changes in urinary pH alter the rate of mexiletine excretion.  Elimination is accelerated with an acidic urinary pH and slowed with an alkaline urinary pH.  Therapy with mexiletine should be administered cautiously in patients with significant renal impairment.  Clinical monitoring of cardiac function (ECG) and renal function is recommended.', '2', 'Wang T, Wuellner D, Woosley RL, Stone WJ "Pharmacokinetics and nondialyzability of mexiletine in renal failure." Clin Pharmacol Ther 37 (1985):  649-53|El Allaf D, Henrard L, Crochelet L, Delapierre D, Carlier J, Dresse A "Pharmacokinetics of mexiletine in renal insufficiency." Br J Clin Pharmacol 14 (1982):  431-5|Guay DR, Meatherall RC, Ferguson I, Smith H, Light B "Mexiletine clearance during peritoneal dialysis ." Br J Clin Pharmacol 19 (1985):  857-8|Jones TE, Reece PA, Fisher GC "Mexiletine removal by peritoneal dialysis." Eur J Clin Pharmacol 25 (1983):  839-40|"Product Information. Mexitil (mexiletine)." Boehringer-Ingelheim  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7754, 6075, 'Mexiletine', 'Seizures', 'Seizures have been reported rarely during mexiletine therapy in patients with and without a history of seizures.  Therapy with mexiletine should be administered cautiously in patients with or predisposition to seizure disorders.', '2', '"Product Information. Mexitil (mexiletine)." Boehringer-Ingelheim  (2001):', 'DDInter', 0);
