INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7804, 690, 'Metronidazole (topical)', 'Hematologic Diseases', 'Metronidazole may be systemically absorbed when applied to the skin or mucosal membranes.  The use of metronidazole has rarely been associated with hematologic adverse effects such as mild, transient leukopenia, thrombocytopenia, and bone marrow aplasia.  The manufacturers recommend that therapy with metronidazole be administered cautiously in patients with evidence of or a history of blood dyscrasias.', '2', 'Smith JA "Neutropenia associated with metronidazole therapy." Can Med Assoc J 123 (1980):  202|White CM, Price JJ, Hunt KM "Bone marrow aplasia associated with metronidazole." Br Med J 280 (1980):  647|"Product Information. MetroGel (metroNIDAZOLE topical)." Galderma Laboratories Inc  (2022):', 'DDInter', 0);
