INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8001, 6077, 'Metronidazole', 'Hematologic Diseases', 'The use of nitroimidazoles (e.g., metronidazole, tinidazole) has rarely been associated with hematologic adverse effects such as mild, transient leukopenia, thrombocytopenia, and bone marrow aplasia.  The manufacturers recommend that therapy with nitroimidazoles be administered cautiously in patients with evidence of or a history of blood dyscrasias, and that total and differential leukocyte counts be performed before and after treatment with these drugs, particularly in patients receiving repeated courses of therapy.', '3', 'Smith JA "Neutropenia associated with metronidazole therapy." Can Med Assoc J 123 (1980):  202|White CM, Price JJ, Hunt KM "Bone marrow aplasia associated with metronidazole." Br Med J 280 (1980):  647|"Product Information. Flagyl (metronidazole)." Searle  (2002):|"Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc  (2004):', 'DDInter', 0);
