INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8322, 8160, 'Midodrine', 'Liver Diseases', 'Midodrine, a prodrug, undergoes bioactivation in a variety of tissues to desglymidodrine, an alpha-1 agonist.  The liver functions in the metabolism of midodrine and desglymidodrine.  The pharmacokinetic disposition of midodrine has not been studied in patients with hepatic impairment.  Therapy with midodrine should be administered cautiously in patients with compromised hepatic function.  Clinical monitoring of hepatic function prior to initiation of therapy.', '2', '"Product Information. ProAmatine (midodrine)." Roberts Pharmaceutical Corporation  (2001):', 'DDInter', 0);
