INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8414, 2158, 'Mineral oil', 'Intestinal Obstruction', 'The use of laxatives is contraindicated in patients with intestinal obstruction disorders.  Patients with intestinal obstruction disorders may need their underlying condition treated to correct the constipation.  Some laxatives require reduction in the colon to their active form to be effective which may be a problem in patients with intestinal obstruction.', '3', '"Product Information. Dulcolax (bisacodyl)." Ciba Self-Medication Inc  (2001):|"Product Information. Fleet Bisacodyl (bisacodyl)." Fleet|"Product Information. Kondremul Plain (mineral oil)." Bristol-Myers Squibb|"Product Information. Fleet Mineral Oil Enema (mineral oil)." Fleet  (2001):|"Product Information. Citrucel (methylcellulose)." SmithKline Beecham  (2001):|"Product Information. Fleet Babylax (glycerin)." Alcon Laboratories Inc  (2001):|"Product Information. SenoSol-X (senna)." Apothecon Inc  (2022):|"Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories  (2010):', 'DDInter', 0);
