INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16049, 4480, 'Phenylephrine', 'Glaucoma', 'Sympathomimetic agents can induce transient mydriasis via stimulation of alpha-1 adrenergic receptors.  In patients with anatomically narrow angles or narrow-angle glaucoma, pupillary dilation can provoke an acute attack.  In patients with other forms of glaucoma, mydriasis may occasionally increase intraocular pressure.  Therapy with sympathomimetic agents should be administered cautiously in patients with or predisposed to glaucoma, particularly narrow-angle glaucoma.', '2', 'Covington TR, eds., Lawson LC, Young LL "Handbook of Nonprescription Drugs." Washington, DC: American Pharmaceutical Association  (1993):|"Product Information. Sudafed (pseudoephedrine)." Glaxo Wellcome  (2001):|Fraunfelder FT, Fraunfelder FW; Randall JA "Drug-Induced Ocular Side Effects" Boston, MA: Butterworth-Heinemann  (2001):', 'DDInter', 0);
