INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16104, 2177, 'Phenytoin', 'Porphyrias', 'The use of phenytoin has rarely been associated with exacerbation of porphyria.  Therapy with phenytoin should be administered cautiously in patients with porphyria.  The same precaution should also be observed with other hydantoin anticonvulsants (i.e. ethotoin and mephenytoin) because of their structural and pharmacological similarities to phenytoin.', '3', '"Product Information. Dilantin (phenytoin)." Parke-Davis  (2001):|"Product Information. Cerebyx (fosphenytoin)." Parke-Davis  (2001):|Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):', 'DDInter', 0);
