INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16275, 2177, 'Phenytoin', 'Thyroid Diseases', 'Phenytoin may decrease serum PBI (protein-bound iodine) levels without associated thyroid disturbance.  Free thyroxine concentrations may also be decreased, while resin or red cell T3 uptake values may be increased.  Clinicians should be cognizant of these effects when prescribing or administering phenytoin therapy to patients with thyroid disorders.', '2', 'Isojarvi JI, Pakarinen AJ, Myllyla VV "Thyroid function with antiepileptic drugs." Epilepsia 33 (1992):  142-8|"Product Information. Dilantin (phenytoin)." Parke-Davis  (2001):|"Product Information. Cerebyx (fosphenytoin)." Parke-Davis  (2001):', 'DDInter', 0);
