INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16374, 4411, 'Pilocarpine (ophthalmic)', 'Retinal Detachment', 'The use of miotic agents may occasionally cause retinal detachment due to drug-induced ciliary or accommodative spasm, which causes the lens and vitreous to move forward and create a retinal tear.  Therapy with miotic agents should be administered with extreme caution, if at all, in patients with risk factors for retinal detachment, such as old age, retinal degenerative changes or other retinal disorders, aphakia, prior cataract extraction, or a history of severe myopia or retinal detachment.', '3', 'American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association;  (1994):|Zimmerman TJ, Wheeler TM "Miotics: side effects and ways to avoid them." Ophthalmology 89 (1982):  76-80|Benedict WL, Shami M "Impending macular hole associated with topical pilocarpine." Am J Ophthalmol 114 (1992):  765-6|"Product Information. Phospholine Iodide (echothiophate iodide ophthalmic)." Wyeth-Ayerst Laboratories|"Product Information. Humorsol Ocumeter (demecarium bromide ophthalmic)." Merck & Co., Inc|"Product Information. Eserine Sulfate Ophthalmic (PHYSostigmine ophthalmic)." Ciba Vision Ophthalmics|Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):|"Product Information. Isopto Carpine (pilocarpine ophthalmic)." Alcon Laboratories Inc  (2001):|"Product Information. Isopto Carbachol (carbachol ophthalmic)." Alcon Laboratories Inc  (2001):', 'DDInter', 0);
