INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16402, 20843, 'Axitinib', 'Hemorrhage', 'Fatal hemorrhagic events, including cerebral hemorrhage, hematuria, hemoptysis, lower gastrointestinal hemorrhage, and melena have been reported with the use of axitinib.  Therapy with axitinib has not been studied in patients who have evidence of untreated brain metastasis or recent active gastrointestinal bleeding and should not be used in these patients.  If any bleeding requires medical intervention, it is recommended to temporarily interrupt the axitinib dose and to stop treatment at least 24 hours prior to scheduled surgery.  The decision to resume therapy after surgery should be based on clinical judgment of adequate wound healing.', '3', '"Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
