INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16406, 20843, 'Axitinib', 'Peptic Ulcer Perforation', 'Therapy with axitinib should be used with caution in patients who may be at increased risk for gastrointestinal perforation, such as those with a history of diverticulitis.  Patients presenting with new onset of abdominal symptoms should be evaluated promptly for early identification of gastrointestinal perforation.  It is recommended to monitor for symptoms of gastrointestinal perforation or fistula periodically throughout the treatment.', '2', '"Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16407, 20844, 'Axitinib', 'Peptic Ulcer Perforation', 'Therapy with axitinib should be used with caution in patients who may be at increased risk for gastrointestinal perforation, such as those with a history of diverticulitis.  Patients presenting with new onset of abdominal symptoms should be evaluated promptly for early identification of gastrointestinal perforation.  It is recommended to monitor for symptoms of gastrointestinal perforation or fistula periodically throughout the treatment.', '2', '"Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16408, 20843, 'Axitinib', 'Hepatic Insufficiency', 'The systemic exposure to axitinib is higher in subjects with moderate hepatic impairment.  Dose adjustment is recommended in patients with moderate hepatic impairment.  No dose adjustment is required in patients with mild hepatic impairment.  The use of axitinib has not been studied in subjects with severe hepatic impairment.  It is recommended to monitor liver enzymes and bilirubin before the start of therapy and periodically thereafter.', '2', '"Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
