INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16410, 20843, 'Axitinib', 'Hypertension', 'The use of axitinib may causes hypertension.  Blood pressure should be well-controlled prior to initiating axitinib and treated as needed with standard anti-hypertensive therapy.  It is recommended to reduce the dose in case of persistent hypertension despite use of anti-hypertensive medications and to discontinue therapy if hypertension is severe and persistent despite these measures.  Discontinuation of therapy should be considered if there is evidence of hypertensive crisis.  Close monitoring is recommended.', '2', '"Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16411, 20844, 'Axitinib', 'Hypertension', 'The use of axitinib may causes hypertension.  Blood pressure should be well-controlled prior to initiating axitinib and treated as needed with standard anti-hypertensive therapy.  It is recommended to reduce the dose in case of persistent hypertension despite use of anti-hypertensive medications and to discontinue therapy if hypertension is severe and persistent despite these measures.  Discontinuation of therapy should be considered if there is evidence of hypertensive crisis.  Close monitoring is recommended.', '2', '"Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16412, 20843, 'Axitinib', 'Proteinuria', 'Proteinuria has been reported in patients taking axitinib.  It is recommended to monitor for proteinuria before initiation of therapy, and periodically thereafter.  Reduce dose or temporarily interrupt therapy in patients who develop moderate to severe proteinuria.', '2', '"Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
