INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16416, 20843, 'Axitinib', 'Posterior Leukoencephalopathy Syndrome', 'Reversible posterior leukoencephalopathy syndrome (RPLS) has been reported in patients receiving axitinib.  Consider this syndrome in any patient presenting with seizures, headache, visual disturbances, confusion or altered mental function.  It is recommended to discontinue therapy in patients developing RPLS.  The safety of reinitiating therapy in patients previously experiencing RPLS is not known.  Care and close monitoring should be considered in these patients.', '2', '"Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
