INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16418, 20843, 'Axitinib', 'Thromboembolism', 'Arterial and venous thromboembolic events, including transient ischemic attack, cerebrovascular accident, myocardial infarction, pulmonary embolism, deep vein thrombosis, retinal occlusion, vein thrombosis, and deaths have been reported with the use of axitinib.  Use with caution in patients who are at risk for, or who have a history of, these events.  The use of axitinib has not been studied in patients who had an arterial or venous thromboembolic event within the previous 12 months.  It is recommended to monitor closely for signs and symptoms of thromboembolism.', '2', '"Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
