INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16420, 20843, 'Axitinib', 'Hypothyroidism', 'Thyroid level fluctuations that include hypothyroidism and hyperthyroidism have been reported with the use of axitinib.  It is recommended to monitor thyroid function before initiation of, and periodically throughout treatment and to maintain euthyroid state according to standard medical practice.', '2', '"Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'DDInter', 0);
