INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16446, 24220, 'Pimozide', 'Central Nervous System Diseases', 'The use of most miscellaneous antipsychotics is contraindicated in patients with severe central nervous system depression or comatose states from any cause (e.g., lesion, disease, drug or alcohol induced).', '3', '"Product Information. Haldol (haloperidol)." McNeil Pharmaceutical  (2002):|"Product Information. Moban (molindone)." Gate Pharmaceuticals  (2001):|"Product Information. Orap (pimozide)." Gate Pharmaceuticals|"Product Information. Adasuve (loxapine)." Teva Pharmaceuticals USA  (2015):', 'DDInter', 0);
