INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16454, 24220, 'Pimozide', 'Respiratory Insufficiency', 'The use of neuroleptic agents is contraindicated in comatose patients and patients with severe central nervous system depression.  Neuroleptic agents may potentiate the CNS and respiratory depression in these patients.', '3', '"Product Information. Haldol (haloperidol)." McNeil Pharmaceutical  (2002):|"Product Information. Navane (thiothixene)." Roerig Division  (2001):|Vetter PH, Proppe DG "Clozapine-induced coma." J Nerv Ment Dis 180 (1992):  58-9|"Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals  (2001):|"Product Information. Moban (molindone)." Gate Pharmaceuticals  (2001):|"Product Information. Orap (pimozide)." Gate Pharmaceuticals|"Product Information. Loxitane C (loxapine)." Apothecon Inc  (2022):', 'DDInter', 0);
