INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16474, 24220, 'Pimozide', 'Liver Diseases', 'Pimozide is extensively metabolized by the liver, and both parent drug and metabolites are eliminated by the kidney.  Patients with impaired renal and/or hepatic function may be at greater risk for adverse effects due to drug and metabolite accumulation.  Therapy with pimozide should be administered cautiously in such patients.', '2', 'Pinder RM, Brogden RN, Swayer R, Speight TM, Spencer R, Avery GS "Pimozide: a review of its pharmacological properties and therapeutic uses in psychiatry." Drugs 12 (1976):  1-40|"Product Information. Orap (pimozide)." Gate Pharmaceuticals', 'DDInter', 0);
