INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16530, 121, 'Pioglitazone', 'Edema', 'Thiazolidinediones can cause dose-related edema.  Therapy with thiazolidinediones should be administered cautiously in patients at risk for congestive heart failure as well as those with fluid overload or other conditions that may be adversely affected by excess fluid such as hypertension.  Patients should be monitored for signs and symptoms of heart failure such as dyspnea, swelling of legs or ankles, and weight gain.', '2', '"Product Information. Rezulin (troglitazone)." Parke-Davis  (2001):|"Product Information. Avandia (rosiglitazone)." SmithKline Beecham  (2001):|"Product Information. Actos (pioglitazone)." Takeda Pharmaceuticals America  (2001):|Thomas ML, Lloyd SJ "Pulmonary edema associated with rosiglitazone and troglitazone." Ann Pharmacother 35 (2001):  123-4', 'DDInter', 0);
