INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8501, 29838, 'Mirtazapine', 'Pheochromocytoma', 'Tricyclic and tetracyclic antidepressants (TCAs) may potentiate the effects of circulating catecholamines.  Enhanced sympathetic activity can provoke hypertensive crises in patients with pheochromocytoma or other tumors of the adrenal medulla, such as some neuroblastomas.  Therapy with TCAs should be administered cautiously in patients with these tumors.', '3', '"Product Information. Pamelor (nortriptyline)." Sandoz Pharmaceuticals Corporation  (2002):|"Product Information. Elavil (amitriptyline)." Stuart Pharmaceuticals  (2002):|"Product Information. Norpramin (desipramine)." Hoechst Marion Roussel  (2002):|"Product Information. Sinequan (doxepin)." Roerig Division  (2002):|"Product Information. Tofranil (imipramine)." Novartis Pharmaceuticals  (2002):|"Product Information. Anafranil (clomipramine)." Basel Pharmaceuticals  (2001):|"Product Information. Remeron (mirtazapine)." Organon  (2001):|"Product Information. Asendin (amoxapine)." Lederle Laboratories  (2001):|"Product Information. Ludiomil (maprotiline)." Ciba-Geigy Pharmaceuticals  (2001):|Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):|"Product Information. Surmontil (trimipramine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Vivactil (protriptyline)." Merck & Co., Inc  (2001):', 'DDInter', 0);
