INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8530, 8238, 'Mirtazapine', 'Neutropenia', 'The use of tetracyclic antidepressants has been associated with neutropenia (ANC < 500/mm3) and agranulocytosis (ANC < 500/mm3) with associated signs and symptoms,( e.g., fever, infection, etc.).  Patients with preexisting neutropenia or agranulocytosis should be monitored closely during therapy for further decreases in white blood cell (WBC) counts.  Treatment should be discontinued in any patient who develops a sore throat, fever, stomatitis, or other signs of infection along with a low WBC count.', '3', '"Product Information. Remeron (mirtazapine)." Organon  (2001):|Montgomery SA "Safety of mirtazapine: a review." Int Clin Psychopharmacol 10(suppl 4 (1995):  37-45|"Product Information. Ludiomil (maprotiline)." Ciba-Geigy Pharmaceuticals  (2001):|Settle EC "Antidepressant drugs: disturbing and potentially dangerous adverse effects." J Clin Psychiatry 59 Suppl 16 (1998):  25-30', 'DDInter', 0);
