INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8882, 15465, 'Modafinil', 'Hypertension', 'CNS stimulant medications have shown to increase blood pressure, and their use might be contraindicated in patients with severe hypertension.  Caution should be used when administering to patients with preexisting high blood pressure and other cardiovascular conditions.  All patients under treatment should be regularly monitored for changes in blood pressure and heart rate.', '3', '"Product Information. Fastin (phentermine)." SmithKline Beecham  (2001):|"Product Information. Provigil (modafinil)." Cephalon, Inc  (2001):|"Product Information. Dopram (doxapram)." West Ward Pharmaceutical Corporation  (2001):|"Product Information. Desoxyn (methamphetamine)." Abbott Pharmaceutical  (2001):|"Product Information. Dexedrine (dextroamphetamine)." SmithKline Beecham  (2001):|"Product Information. Didrex (benzphetamine)." Pharmacia and Upjohn  (2001):|"Product Information. Tenuate (diethylpropion)." Aventis Pharmaceuticals  (2001):|"Product Information. Focalin (dexmethylphenidate)." Mikart Inc  (2001):|"Product Information. Concerta (methylphenidate)." Alza  (2002):|"Product Information. Strattera (atomoxetine)." Lilly, Eli and Company  (2002):|"Product Information. Vyvanse (lisdexamfetamine)." Shire US Inc  (2007):|"Product Information. Nuvigil (armodafinil)." Cephalon Inc  (2007):|"Product Information. Phendimetrazine Tartrate SR (phendimetrazine)." Sandoz Inc  (2012):', 'DDInter', 0);
