INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8915, 22796, 'Moexipril', 'Diseases requiring hemodialysis', 'Anaphylactoid reactions have been reported in patients undergoing hemodialysis with high-flux polyacrylonitrile membranes and treated concomitantly with an ACE inhibitor.  The frequency and mechanism of this interaction have not been established, and it is not known whether the interaction occurs with other membrane types.  Therapy with ACE inhibitors should be administered cautiously in patients requiring hemodialysis.', '3', '"Product Information. Lotensin (benazepril)." Ciba-Geigy Pharmaceuticals  (2002):|"Product Information. Capoten (captopril)." Bristol-Myers Squibb  (2002):|"Product Information. Vasotec (enalapril)." Merck & Co., Inc  (2002):|"Product Information. Prinivil (lisinopril)." Merck & Co., Inc  (2002):|"Product Information. Accupril (quinapril)." Parke-Davis  (2001):|"Product Information. Monopril (fosinopril)." Bristol-Myers Squibb  (2001):|"Product Information. Altace (ramipril)." Hoechst Marion Roussel  (2001):|"Product Information. Aceon (perindopril)." Solvay Pharmaceuticals Inc  (2001):|"Product Information. Univasc (moexipril)." Schwarz Pharma  (2001):|"Product Information. Mavik (trandolapril)." Knoll Pharmaceutical Company  (2001):', 'DDInter', 0);
