INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (18135, 8197, 'Prazosin', 'Liver Diseases', 'Little data exist concerning the pharmacokinetic disposition of prazosin in patients with liver disease.  Prazosin is known to be metabolized by the liver and subsequently excreted in the bile, primarily as metabolites.  Some of the metabolites are pharmacologically active and possess 10% to 25% of the hypotensive activity of prazosin.  Therapy with prazosin should be administered cautiously in patients with significantly impaired hepatic or biliary function, since drug and/or metabolite accumulation may occur.', '2', 'Bateman DN, Rawlins MD "Prazosin concentration-effect, metabolism and biological activity of metabolites." Br J Clin Pharmacol 11 (1981):  221-', 'DDInter', 0);
