INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9072, 1008, 'Montelukast', 'Respiration Disorders', 'Patients with asthma on therapy with montelukast may present with systemic eosinophilia, sometimes presenting with clinical features of vasculitis consistent with Churg-Strauss syndrome, a condition which is often treated with systemic corticosteroid therapy.  Prescribers should be alert to eosinophilia, vasculitic rash, worsening pulmonary symptoms, cardiac complications, and/or neuropathy presenting in their patients.  Care should be taken before using this agent in patients with history of pulmonary disorders.', '2', '"Product Information. Singulair (montelukast)." Merck & Co., Inc  (2001):', 'DDInter', 0);
