INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19722, 23721, 'Propafenone', 'Heart Failure', 'The use of propafenone is contraindicated in patients with uncontrolled congestive heart failure, cardiogenic shock, bradycardia, or marked hypotension.  Propafenone can exacerbate congestive heart failure by beta blockade of sympathetic stimulation necessary for circulatory support.  Propafenone also has negative inotropic activity that can depress cardiac output and cardiac index.', '3', 'Ravid S, Podrid PJ, Lampert S, Lown B "Congestive heart failure induced by six of the newer antiarrhythmic drugs." J Am Coll Cardiol 14 (1989):  1326-30|"Product Information. Rythmol (propafenone)." Knoll Pharmaceutical Company', 'DDInter', 0);
