INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19734, 23721, 'Propafenone', 'Liver Diseases', 'Propafenone is extensively metabolized by the liver.  The bioavailability of propafenone in patients with normal hepatic function is approximately 3% to 40%, but increases to approximately 70% in patients with severe hepatic impairment.  Therapy with propafenone should be administered cautiously and the dosage reduced to 20% to 30% of that given patients with normal hepatic function.  Clinical monitoring of cardiac function (ECG) and hepatic function is recommended.', '2', 'Mondardini A, Pasquino P, Bernardi P, Aluffi E, Tartaglino B, Mazzucco G, Bonino F, Verme G, Negro F "Propafenone-induced liver injury: report of a case and review of the literature." Gastroenterology 104 (1993):  1524-6|Spinler SA, Elder CA, Kindwall KE "Propafenone-induced liver injury." Ann Pharmacother 26 (1992):  926-8|"Product Information. Rythmol (propafenone)." Knoll Pharmaceutical Company', 'DDInter', 0);
