INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19737, 23721, 'Propafenone', 'Kidney Diseases', 'Propafenone is partially eliminated by the kidney and approximately 50% of its metabolites are excreted in the urine.  Therapy with propafenone should be administered cautiously in patients with renal impairment, monitoring for signs of overdosage.', '2', 'Chan GL, Axelson JE, Price JD, et al. "In vitro protein binding of propafenone in normal and uraemic human sera." Eur J Clin Pharmacol 36 (1989):  495-9|Burgess E, Duff H, Wilkes P "Propafenone disposition in renal insufficiency and renal failure." J Clin Pharmacol 29 (1989):  112-3|Burgess ED, Duff HJ "Hemodialysis removal of propafenone." Pharmacotherapy 9 (1989):  331-3|"Product Information. Rythmol (propafenone)." Knoll Pharmaceutical Company', 'DDInter', 0);
