INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19772, 11398, 'Propylthiouracil', 'Hemorrhagic Disorders', 'Thioamide antithyroid agents may interfere with the action of vitamin K and have been reported in isolated cases to induce hypoprothombinemia and bleeding.  Myelosuppression rarely resulting in thrombocytopenia has also been reported.  Therapy with thioamide derivatives should be administered cautiously in patients with significant active bleeding or a hemorrhagic diathesis, including hemostatic and/or coagulation defects associated with hemophilia, vitamin K deficiency, hypoprothombinemia, thrombocytopenia, thrombocytopathy, or severe hepatic impairment.  Clinical monitoring of prothrombin time or INR is recommended.', '3', 'Sammon TJ, Peden VH, Witzleben C, King JP "Disseminated intravascular coagulation complicating propylthiouracil therapy. A case description of a 16-year-old girl." Clin Pediatr (Phila) 10 (1971):  739-42|"Product Information. Propylthiouracil (propylthiouracil)." Lederle Laboratories  (2001):|"Product Information. Tapazole (methimazole)." King Pharmaceuticals Inc  (2001):', 'DDInter', 0);
