INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19774, 11398, 'Propylthiouracil', 'Hypothyroidism', 'Propylthiouracil may cause hypothyroidism and it should be used with caution in patients with an impaired thyroid function.  Routine monitoring of TSH and free T4 levels with adjustments in dosing to maintain a euthyroid state is recommended.', '2', '"Product Information. Propylthiouracil (propylthiouracil)." Lederle Laboratories  (2001):', 'DDInter', 0);
