INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19792, 3728, 'Propofol', 'Kidney Diseases', 'At high doses (2-3 grams per day), EDTA has been reported, on rare occasions, to be toxic to the renal tubules.  Studies to-date, in patients with normal or impaired renal function have not shown any alteration in renal function with propofol Injectable Emulsion containing 0.005% disodium edetate.  In patients at risk for renal impairment, urinalysis and urine sediment should be checked before initiation of sedation and then be monitored on alternate days during sedation.', '2', '"Product Information. Diprivan (propofol)." Astra-Zeneca Pharmaceuticals  (2001):', 'DDInter', 0);
