INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19808, 3728, 'Propofol', 'Deficiency Diseases', 'Reports of mean urinary zinc loss during propofol therapy was approximately 2.5 to 3.0 mg/day in adult patients and 1.5 to 2.0 mg/day in pediatric patients.  Practitioners should consider the administration of supplemental zinc during prolonged therapy with propofol in patients who are predisposed to zinc deficiency, such as those with burns, diarrhea, and/or major sepsis.  Monitoring is recommended in these patients.', '2', '"Product Information. Diprivan (propofol)." Astra-Zeneca Pharmaceuticals  (2001):', 'DDInter', 0);
